import React, { useContext } from "react";
import { Box, Button, TextField, Typography } from "@mui/material";
import { useForm } from "react-hook-form";
import { useNavigate } from "react-router-dom";
import { toast } from "react-toastify";
import { Authenticate } from "./AuthContext";
import "./auth.css";

const Login = () => {
  const navigate = useNavigate();
  const { register, handleSubmit, formState: { errors } } = useForm();
  const { setAuth } = useContext(Authenticate);

  // Form validation function
  function FormValidate(data) {
    if (data.Email === "user@gmail.com" && data.Psw === "1234") {
      setAuth(true);
      toast.success("Login Successfully!");
      navigate("/");
    } else {
      toast.error("Invalid Credentials");
    }
  }

  return (
    <section className="bg-gray-50 dark:bg-gray-900 flex items-center justify-center h-screen">
      <div className="w-full max-w-md bg-white rounded-lg shadow-lg dark:border dark:bg-gray-800 dark:border-gray-700 p-6">
        <Typography variant="h5" className="text-gray-900 dark:text-white text-center mb-4">
          Login
        </Typography>
        <form className="space-y-4" onSubmit={handleSubmit(FormValidate)}>
          <div>
            <TextField
              fullWidth
              label="Email"
              variant="outlined"
              {...register("Email", { required: "Email is required" })}
              error={!!errors.Email}
              helperText={errors.Email?.message}
            />
          </div>
          <div>
            <TextField
              fullWidth
              label="Password"
              type="password"
              variant="outlined"
              {...register("Psw", { required: "Password is required" })}
              error={!!errors.Psw}
              helperText={errors.Psw?.message}
            />
          </div>
          <Button
            type="submit"
            fullWidth
            variant="contained"
            color="primary"
          >
            Login
          </Button>
        </form>
        <Typography variant="body2" className="text-center text-gray-500 dark:text-gray-400 mt-4">
          Don't have an account? <a href="/signup" className="text-primary-600 dark:text-primary-500">Sign up here</a>
        </Typography>
      </div>
    </section>
  );
};

export default Login;
