export const  Menu= [
    {
      "section_name": "break fast",
      "items": [
        {
          "name": "PPB",
          "description": "WPuff-puff, pap, and beans is a hearty and beloved Cameroonian street food combination that brings together sweet, savory, and comforting flavors.",
          "price": 6.99,
          "image": Image1
        },
        {
          "name": "Eggs Benedict",
          "description": "Egg and spaghetti is a simple yet beloved Cameroonian street food and home dish known for its quick preparation and satisfying taste.",
          "price": 9.49,
          "image": Image2
        },
        {
          "name": "Salade Avocado",
          "description": "The dish typically consists of ripe avocados sliced or diced, mixed with tomatoes, onions, cucumbers, and sometimes hard-boiled eggs.",
          "price": 6.99,
          "image": Image3
        }
      ]
    },
    {
      "section_name": "Entrees",
      "items": [
        {
          "name": "salade",
          "description": "A juicy grilled steak served with garlic mashed potatoes and seasonal vegetables.",
          "price": 18.99,
          "image": Image16
        },
        {
          "name": "fruid salade",
          "description": "Tender grilled chicken breast marinated in herbs and spices.",
          "price": 15.49,
          "image": Image17
        },
        {
          "name": "cakes sal",
          "description": "Pan-seared salmon fillet served with lemon butter sauce.",
          "price": 17.99,
          "image": Image18
        }
      ]
    },
    {
      "section_name": "Traditional dishes",
      "items": [
        {
          "name": "SAUCE JAUNE AND TARO",
          "description": "The dish consists of tender taro (a starchy root vegetable) typically boiled and served with Sauce Jaune, a rich and flavorful yellow sauce made from crushed egusi (melon seeds), palm oil, and a blend of aromatic spices.",
          "price": 18.99,
          "image": Image6
        },
        {
          "name": "Koki",
          "description": "Koki is a cherished Cameroonian dish made primarily from black-eyed peas (cowpeas) or koki beans, blended with red palm oil and steamed to perfection in banana or plantain leaves.",
          "price": 16.99,
          "image": Image7
        },
        {
          "name": "eru",
          "description": "made with finely shredded eru leaves (a type of wild spinach) or sometimes waterleaf, cooked together with a generous amount of palm oil, crayfish, and assorted meats or fish, including smoked fish, cow skin (kanda), and beef.",
          "price": 14.99,
          "image": Image8
        }
      ]
    },
    {
      "section_name": "Apetisance",
      "items": [
        {
          "name": "Spaghetti Carbonara",
          "description": "Classic spaghetti with creamy carbonara sauce, bacon, and parmesan cheese.",
          "price": 12.99,
          "image": Image10
        },
        {
          "name": "Penne Primavera",
          "description": "Penne pasta tossed with fresh vegetables in a light olive oil and garlic sauce.",
          "price": 11.99,
          "image": Image11
        },
        {
          "name": "Chicken Alfredo",
          "description": "Grilled chicken and fettuccine pasta in a rich alfredo sauce.",
          "price": 13.99,
          "image": Image9
        }
      ]
    },
    {
      "section_name": "consisten dishes",
      "items": [
        {
          "name": " Ndolé",
          "description": "Ndolé is a traditional Cameroonian dish made with stewed bitter leaves (commonly Vernonia amygdalina) cooked with groundnuts (peanuts) and flavored with various spices.",
          "price": 27.99,
          "image": Image13
        },
        {
          "name": "Okok",
          "description": "It is made from the leaves of the wild spinach (Gnetum africanum), locally called okok or eru, which are finely chopped and cooked with waterleaf to create a rich, slightly slimy consistency.",
          "price": 32.99,
          "image": Image14
        },
        {
          "name": "Poulet DG",
          "description": "A luxurious Cameroonian dish that combines fried chicken with sautéed vegetables and ripe plantains.",
          "price": 19.99,
          "image": Image15
        }
      ]
    }
  ]

